/* ─────────────────────────────────────────────────────────────────
 *  Illustration Maker — Asset Library
 *
 *  Each asset points to an SVG/PNG in public/assets/illustrations/.
 *  Placeholder SVGs are generated by scripts/generate-illustration-placeholders.mjs.
 *  Replace them with real scientific illustration PNGs over time.
 * ────────────────────────────────────────────────────────────────── */

export interface IllustrationAsset {
  id: string;
  language: string;
  name: string;
  category: string;
  tags: string[];
  /** Path relative to /public — used as the image src */
  path: string;
  /** Natural width of the asset in px (used for initial sizing on canvas) */
  width: number;
  /** Natural height of the asset in px */
  height: number;
}

export interface AssetCategory {
  id: string;
  /** Display label */
  name: string;
  /** Lucide icon name */
  icon: string;
  /** Tailwind showcase color key for styling the category tab */
  color: string;
}

