/* ── Helpers ────────────────────────────────────────────────────── */

export function getAssetsByCategory(categoryId: string): IllustrationAsset[] {
  return illustrationAssets.filter((a) => a.category === categoryId);
}

export function searchAssets(query: string): IllustrationAsset[] {
  const q = query.toLowerCase().trim();
  if (!q) return illustrationAssets;
  return illustrationAssets.filter(
    (a) =>
      a.name.toLowerCase().includes(q) ||
      a.tags.some((t) => t.includes(q))
  );
}
